import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {

  constructor(){}

  ngOnInit(): void {
    document.body.style.backgroundColor="white";
  }

  products = [
    {
      name: 'Wireless Headphones',
      price: 150,
      description: 'High-quality wireless headphones with noise cancellation.',
      category: 'Electronics',
      discountPrice: 120,
      image: 'assets/images/product3.jpg',
      quantity: 1
    },
    {
      name: 'Running Shoes',
      price: 80,
      description: 'Comfortable and lightweight running shoes for daily wear.',
      category: 'Footwear',
      discountPrice: 65,
      image: 'assets/images/product4.png',
      quantity: 2
    }
  ];

  incrementQuantity(product: any): void {
    product.quantity++;
  }

  decrementQuantity(product: any): void {
    if (product.quantity > 1) {
      product.quantity--;
    }
  }

  calculateTotal(): number {
    return this.products.reduce((total, product) => {
      return total + product.discountPrice * product.quantity;
    }, 0);
  }

  onRemove(item:any,index:any){

  }


}
<div class="cart-container">
    <h2>Product Cart</h2>
    <div class="cart-item" *ngFor="let product of products;index as i">
      <img [src]="product.image" alt="{{ product.name }}" class="product-image" />
      <div class="product-details">
        <h3>{{ product.name }}</h3>
        <p class="category">Category: {{ product.category }}</p>
        <p class="description">{{ product.description }}</p>
        <p class="price">Price: {{ product.price |currency:'INR'  }}</p>
        <p class="discount">Discount Price: {{ product.discountPrice|currency:'INR' }}</p>
        <div class="quantity">
          <button (click)="decrementQuantity(product)">-</button>
          <span>{{ product.quantity }}</span>
          <button (click)="incrementQuantity(product)">+</button>

          <button (click)="onRemove(product,i)" id="remove">Remove</button>

        </div>
      </div>
    </div>

    <div class="cart-summary">
      <h3>Total Price: ${{ calculateTotal() }}</h3>
      <button class="checkout-button">Proceed to Checkout</button>
    </div>
  </div>
